	color: <?php echo esc_attr( $financeup_top_color);?>;
}
/*=== navbar menu Background hover color ===*/ 
.navbar-wp .navbar-nav > li > a:hover, .navbar-wp .navbar-nav > li > a:focus, .navbar-wp .navbar-nav > li > a.active {
