?>
<style type="text/css">
/*==================== footer background ====================*/
footer {
	background: <?php echo esc_attr( $financeup_footer_background);?>;
}
footer .ta-footer-copyright {
	background: <?php echo esc_attr( $financeup_footer_copy_background);?>;
}
